.tab-bar {
  height: 31px;
  box-shadow: inset 0 -1px 0 #c7c7c7;
  background: #d1d1d1;
  overflow-x: auto;
  overflow-y: hidden;
}
.tab-bar::-webkit-scrollbar {
  display: none;
}
.tab-bar .tab {
  position: relative;
  top: 0;
  max-width: 160px;
  min-width: 28px;
  height: 30px;
  line-height: 30px;
  padding: 0;
  margin: 0;
  color: #5c3d3d;
  background: #d1d1d1;
  border-right: 1px solid #c7c7c7;
  opacity: 0.7;
  transition: opacity .3s;
}
.tab-bar .tab:last-of-type {
  border-right-color: transparent;
}
.tab-bar .tab:hover .close-icon {
  transform: scale(1);
}
.tab-bar .tab .title {
  position: relative;
  z-index: 1;
  padding: 0 8px;
}
.tab-bar .tab .close-icon {
  right: 0;
  z-index: 3;
  margin-right: 8px;
  text-align: right;
  line-height: 28px;
  color: rgba(46, 46, 46, 0.7);
  background: inherit;
  transform: scale(0);
  transition: transform .1s;
}
.tab-bar .tab .close-icon:hover {
  color: inherit;
}
.tab-bar .tab .close-icon:active {
  opacity: .3;
}
.tab-bar .tab.active {
  z-index: 1;
  flex: 2 0 auto;
  padding-right: 10px;
  background-color: #e0e0e0;
  opacity: 1;
}
.tab-bar .tab.active:last-of-type {
  border-right-color: #c7c7c7;
}
.tab-bar .tab.active .close-icon {
  transform: scale(1);
}
.tab-bar .tab.active:after {
  content: "";
  position: absolute;
  left: 0;
  right: 0;
  bottom: -1px;
  border-bottom: 1px solid #e0e0e0;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  right: 0;
  top: 12px;
  width: 8px;
  height: 8px;
  border-color: #dc1818;
  transform: scale(1);
}
.tab-bar .tab.modified:hover .close-icon:hover {
  color: #5c3d3d;
}
.tab-bar .tab.is-dragging {
  background: #c2c2c2;
  border-color: transparent;
  opacity: .5;
}
.tab-bar .tab.is-dragging .close-icon,
.tab-bar .tab.is-dragging:before,
.tab-bar .tab.is-dragging:after {
  visibility: hidden;
}
.tab-bar .tab.active[data-type="TextEditor"] {
  color: #dbdbdb;
  background-color: #282828;
}
.tab-bar .tab.active[data-type="TextEditor"]:after {
  border-bottom-color: #282828;
}
.tab-bar .placeholder {
  margin: 0;
  height: 30px;
  background: #dc1818;
  pointer-events: none;
}
.tab-bar .placeholder:after {
  top: 10px;
  width: 10px;
  height: 10px;
  margin: 0;
  border-radius: 0;
  border: 5px solid;
  border-color: transparent transparent transparent #dc1818;
  background: transparent;
}
.tab-bar .placeholder:last-child {
  margin-left: -2px;
}
.tab-bar .placeholder:last-child:after {
  margin-left: -10px;
  border-color: transparent #dc1818 transparent transparent;
}
.tab-bar:empty:before,
.tab-bar .tab:before {
  content: "";
  position: absolute;
  pointer-events: none;
  z-index: 2;
  top: 0;
  left: 0;
  width: 2px;
  height: 31px;
  background: #dc1818;
  transform: scaleY(0);
  transition: transform .08s;
}
atom-pane.active .tab-bar:empty:before,
atom-pane.active .tab.active:before {
  transform: scaleY(1);
}
