  bottom: 0;
  height: 8px;
  left: 0px;
  width: 100%;
  background-color: #222222;
  border-top: 8px solid #4a4e52;
  border-bottom: 1px solid #111111;
}
.tab-bar .tab {
  position: relative;
  top: 5px;
  height: 25px;
  line-height: 25px;
  color: #aaaaaa;
  padding-left: 0;
  margin-left: 15px;
  border-top-right-radius: 3px;
  transition: color 0.1s ease-in;
  border: none;
  margin-right: 5px;
}
.tab-bar .tab,
.tab-bar .tab:before {
  background: #32363b;
}
.tab-bar .tab:before {
  content: '';
