  top: 0;
  left: -18px;
  height: 25px;
  width: 18px;
  border-top-left-radius: 3px;
  border: none;
}
.tab-bar .tab .close-icon {
  z-index: 3;
  line-height: 12px;
  background: #7f8285;
  color: #3f4345;
  width: 12px;
  height: 12px;
  text-align: center;
  border-radius: 2px;
  top: 6px;
}
.tab-bar .tab .close-icon:hover {
  background: #dbdbdb;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  top: 9px;
  right: 14px;
  width: 8px;
  height: 8px;
  background: none;
}
.tab-bar .tab:first-child {
  margin-left: 20px;
}
