.tab-bar {
  height: 31px;
  box-shadow: inset 0 -1px 0 #1c1c1c;
  background: #202020;
  overflow-x: auto;
  overflow-y: hidden;
}
.tab-bar::-webkit-scrollbar {
  display: none;
}
.tab-bar .tab {
  position: relative;
  top: 0;
  max-width: 160px;
  min-width: 28px;
  height: 30px;
  line-height: 30px;
  padding: 0;
  margin: 0;
  color: #e0e0e0;
  background: #202020;
  border-right: 1px solid #1c1c1c;
  opacity: 0.5;
  transition: opacity .3s;
}
.tab-bar .tab:last-of-type {
  border-right-color: transparent;
}
.tab-bar .tab:hover .close-icon {
  transform: scale(1);
}
.tab-bar .tab .title {
  position: relative;
  z-index: 1;
  padding: 0 8px;
}
.tab-bar .tab .close-icon {
  right: 0;
  z-index: 3;
  margin-right: 8px;
  text-align: right;
  line-height: 28px;
  color: rgba(120, 120, 120, 0.6);
  background: inherit;
  transform: scale(0);
  transition: transform .1s;
}
.tab-bar .tab .close-icon:hover {
  color: inherit;
}
.tab-bar .tab .close-icon:active {
  opacity: .3;
}
.tab-bar .tab.active {
  z-index: 1;
  flex: 2 0 auto;
  padding-right: 10px;
  background-color: #282828;
  opacity: 1;
}
.tab-bar .tab.active:last-of-type {
  border-right-color: #1c1c1c;
}
.tab-bar .tab.active .close-icon {
  transform: scale(1);
}
.tab-bar .tab.active:after {
  content: "";
  position: absolute;
  left: 0;
  right: 0;
  bottom: -1px;
  border-bottom: 1px solid #282828;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  right: 0;
  top: 12px;
  width: 8px;
  height: 8px;
  border-color: #dd3f3f;
  transform: scale(1);
}
.tab-bar .tab.modified:hover .close-icon:hover {
  color: #e0e0e0;
}
.tab-bar .tab.is-dragging {
  background: #111111;
  border-color: transparent;
  opacity: .5;
}
.tab-bar .tab.is-dragging .close-icon,
.tab-bar .tab.is-dragging:before,
.tab-bar .tab.is-dragging:after {
  visibility: hidden;
}
.tab-bar .tab.active[data-type="TextEditor"] {
  color: #e0e0e0;
  background-color: #282828;
}
.tab-bar .tab.active[data-type="TextEditor"]:after {
  border-bottom-color: #282828;
}
.tab-bar .placeholder {
  margin: 0;
  height: 30px;
  background: #dd3f3f;
  pointer-events: none;
}
.tab-bar .placeholder:after {
  top: 10px;
  width: 10px;
  height: 10px;
  margin: 0;
  border-radius: 0;
  border: 5px solid;
  border-color: transparent transparent transparent #dd3f3f;
  background: transparent;
}
.tab-bar .placeholder:last-child {
  margin-left: -2px;
}
.tab-bar .placeholder:last-child:after {
  margin-left: -10px;
  border-color: transparent #dd3f3f transparent transparent;
}
.tab-bar:empty:before,
.tab-bar .tab:before {
  content: "";
  position: absolute;
  pointer-events: none;
  z-index: 2;
  top: 0;
  left: 0;
  width: 2px;
  height: 31px;
  background: #dd3f3f;
  transform: scaleY(0);
  transition: transform .08s;
}
atom-pane.active .tab-bar:empty:before,
atom-pane.active .tab.active:before {
  transform: scaleY(1);
}
