css:
.absolute-box {
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
.background-color {
  z-index: -2;
}
.background-color:before {
  content: '';
  background: url(atom://pigments/resources/transparent-background.png);
  z-index: -1;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
.background-color:after {
  content: '';
  background: inherit;
  z-index: -1;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
.icon-pigments::before {
  content: '';
  width: 16px;
  height: 16px;
  display: inline-block;
  background-image: url(atom://pigments/resources/icon.svg);
  background-size: 16px;
  vertical-align: middle;
}
atom-text-editor::shadow pigments-markers,
pigments-markers {
  position: relative;
  display: block;
  z-index: 1;
}
atom-text-editor::shadow pigments-markers.above-editor-content,
pigments-markers.above-editor-content {
  z-index: 1;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker,
pigments-markers::shadow pigments-color-marker,
pigments-color-marker {
  display: block;
  position: absolute;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region,
pigments-markers::shadow pigments-color-marker .region,
pigments-color-marker .region {
  position: absolute;
  white-space: pre;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.background,
pigments-markers::shadow pigments-color-marker .region.background,
pigments-color-marker .region.background {
  border-radius: 2px;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.outline,
pigments-markers::shadow pigments-color-marker .region.outline,
pigments-color-marker .region.outline {
  margin-left: -1px;
  margin-top: -2px;
  padding-left: 4px;
  border-radius: 0;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.outline::before,
pigments-markers::shadow pigments-color-marker .region.outline::before,
pigments-color-marker .region.outline::before {
  box-sizing: border-box;
  border: 2px solid transparent;
  border-image: -webkit-repeating-linear-gradient(45deg, #808080 4px, #6a6a6a 4px, #6a6a6a 8px, #808080 8px, #808080 12px) 1 1 1 1 round;
  content: '';
  background-color: inherit;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.outline::after,
pigments-markers::shadow pigments-color-marker .region.outline::after,
pigments-color-marker .region.outline::after {
  box-sizing: border-box;
  content: '';
  border: 2px solid transparent;
  border-color: inherit;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.underline,
pigments-markers::shadow pigments-color-marker .region.underline,
pigments-color-marker .region.underline {
  height: 2px !important;
  margin-top: 1.3em;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.background,
pigments-markers::shadow pigments-color-marker .region.background,
pigments-color-marker .region.background,
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.underline,
pigments-markers::shadow pigments-color-marker .region.underline,
pigments-color-marker .region.underline {
  z-index: -2;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.background:before,
pigments-markers::shadow pigments-color-marker .region.background:before,
pigments-color-marker .region.background:before,
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.underline:before,
pigments-markers::shadow pigments-color-marker .region.underline:before,
pigments-color-marker .region.underline:before {
  content: '';
  background: url(atom://pigments/resources/transparent-background.png);
  z-index: -1;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.background:after,
pigments-markers::shadow pigments-color-marker .region.background:after,
pigments-color-marker .region.background:after,
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker .region.underline:after,
pigments-markers::shadow pigments-color-marker .region.underline:after,
pigments-color-marker .region.underline:after {
  content: '';
  background: inherit;
  z-index: -1;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker.dot,
pigments-markers::shadow pigments-color-marker.dot,
pigments-color-marker.dot {
  width: 1em;
  height: 1em;
  border-radius: 50%;
  vertical-align: bottom;
  transform: translate(0, -50%);
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker.dot:before,
pigments-markers::shadow pigments-color-marker.dot:before,
pigments-color-marker.dot:before {
  content: '';
  background: url(atom://pigments/resources/transparent-background.png);
  background-size: 100%;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker.dot:after,
pigments-markers::shadow pigments-color-marker.dot:after,
pigments-color-marker.dot:after {
  content: '';
  background: inherit;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
atom-text-editor::shadow pigments-markers::shadow pigments-color-marker.dot.square,
pigments-markers::shadow pigments-color-marker.dot.square,
pigments-color-marker.dot.square {
  border-radius: 0;
}
.color-suggestion .label {
  opacity: 1 !important;
}
.color-suggestion-preview {
  display: inline-block;
  vertical-align: middle;
  width: 1.2em;
  height: 1.2em;
  border-radius: 50%;
}
pigments-color-results {
  display: block;
  width: 100%;
}
pigments-color-results atom-panel.preview-pane {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
}
pigments-color-results .match.color-match {
  padding: 2px 4px;
}
pigments-color-results .preview-pane {
  position: relative;
  display: -webkit-flex;
  -webkit-flex-direction: column;
}
pigments-color-results .preview-pane .results-view {
  overflow: auto;
  position: relative;
  padding: 0 10px;
  -webkit-flex: 1;
}
pigments-color-results .preview-pane .results-view .line-number {
  margin-right: 1ex;
  text-align: right;
  display: inline-block;
}
pigments-color-results .preview-pane .results-view .search-result {
  padding-top: 2px;
  padding-bottom: 2px;
  cursor: alias;
}
pigments-color-results .preview-pane .results-view .search-result:hover {
  background: rgba(255, 255, 255, 0.07);
}
pigments-sources-popup .inset-panel {
  margin-bottom: 0.75em;
}
pigments-palette {
  background: #313437;
  color: #aaaaaa;
  font-size: 1.2em;
}
pigments-palette .pigments-palette-list,
pigments-palette .pigments-palette-controls {
  display: -webkit-flex;
  position: relative;
  width: 100%;
}
pigments-palette ol {
  width: 100%;
  padding: 0;
  margin: 0;
}
pigments-palette li {
  padding: 10px;
  list-style: none;
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  display: -webkit-flex;
  -webkit-flex-direction: row;
}
pigments-palette li.pigments-color-group {
  padding: 0;
  border-bottom: none;
  -webkit-flex-direction: column;
}
pigments-palette [data-variable-id] {
  cursor: alias;
  pointer-events: auto;
  padding-left: 1em;
  display: inline-block;
}
pigments-palette [data-variable-id] * {
  pointer-events: none;
}
pigments-palette .path,
pigments-palette .line {
  color: #555555;
}
pigments-palette .pigments-color-group .path {
  display: none;
}
pigments-palette .pigments-color-group-header-content {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  display: inline-block;
  background: #313437;
  padding: 10px;
  width: 100%;
}
pigments-palette .pigments-color-group-header-content.fixed {
  position: fixed;
  width: 100%;
  box-sizing: border-box;
  z-index: 10;
}
pigments-palette .pigments-color-group-header-content.absolute {
  position: absolute;
}
pigments-palette .pigments-palette-list {
  overflow-y: auto;
  background: #1d1f21;
}
pigments-palette .pigments-color,
pigments-palette .pigments-color-details {
  display: -webkit-flex;
}
pigments-palette .pigments-color-details {
  -webkit-flex-flow: column;
  -webkit-flex: 1;
}
pigments-palette .pigments-color {
  -webkit-flex-flow: row;
}
pigments-palette .pigments-color-properties {
  width: 8em;
}
pigments-palette .pigments-color-properties span {
  display: block;
  float: left;
  width: 50%;
  color: #aaaaaa;
}
pigments-palette .pigments-color-properties strong {
  color: #555555;
}
pigments-palette .pigments-color-preview {
  z-index: -2;
  position: relative;
  width: 3em;
  height: 3em;
  margin-right: 10px;
  z-index: 0;
}
pigments-palette .pigments-color-preview:before {
  content: '';
  background: url(atom://pigments/resources/transparent-background.png);
  z-index: -1;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
pigments-palette .pigments-color-preview:after {
  content: '';
  background: inherit;
  z-index: -1;
  border-radius: inherit;
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
}
pigments-palette .pigments-color-preview:before {
  z-index: 0;
}
pigments-palette .pigments-color-preview:after {
  z-index: 1;
}
pigments-palette select {
  margin: 0 0.5em;
  background: #212224;
  border: 1px solid rgba(0, 0, 0, 0.5);
  color: #aaaaaa;
  font-family: 'Lucida Grande', 'Segoe UI', Ubuntu, Cantarell, sans-serif;
}
pigments-palette .settings-view input[type="checkbox"] {
  margin: 0 0.5em 0 0;
  vertical-align: text-bottom;
  font-size: 0.8em;
}
pigments-palette .pigments-palette-panel {
  display: -webkit-flex;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  -webkit-flex-flow: column;
}
pigments-palette .pigments-palette-controls {
  min-height: -webkit-min-content;
}
pigments-palette .pigments-palette-controls-wrapper {
  border-bottom: 1px solid rgba(0, 0, 0, 0.5);
  padding: 10px;
  width: 100%;
}
pigments-palette .input-group-inline {
  margin-right: 1em;
}
