css:
.settings-view {
  display: flex;
}
.settings-view .breadcrumb {
  margin-bottom: 0;
  padding: 24px;
  font-size: 13px;
  color: #999999;
  list-style: none;
}
.settings-view .breadcrumb > li {
  display: inline-block;
}
.settings-view .breadcrumb > li a {
  color: #555555;
}
.settings-view .breadcrumb > li + li:before {
  content: "/\00a0";
  padding: 0 5px;
}
.settings-view .breadcrumb > .active a {
  color: #333333;
}
.settings-view .text {
  color: #555555;
}
.settings-view .alert-info {
  color: #0e88cb;
  border-color: #c0dceb;
  background-color: #e7f2f8;
}
.settings-view .alert-info .alert-link {
  color: inherit;
  text-decoration: underline;
}
.settings-view .alert-success {
  color: #408d29;
  border-color: #cce3c5;
  background-color: #eaf4e7;
}
.settings-view .alert-success .alert-link {
  color: inherit;
  text-decoration: underline;
}
.settings-view .alert-warning {
  color: #d27407;
  border-color: #edd7be;
  background-color: #f8f0e6;
}
.settings-view .alert-warning .alert-link {
  color: inherit;
  text-decoration: underline;
}
.settings-view .alert-error,
.settings-view .alert-danger {
  color: #f50900;
  border-color: #eec2c1;
  background-color: #f9e9e8;
}
.settings-view .alert-error .alert-link,
.settings-view .alert-danger .alert-link {
  color: inherit;
  text-decoration: underline;
}
.settings-view .error-message .alert {
  margin-bottom: 0;
}
.settings-view .error-message .close {
  top: -6px;
}
.settings-view .error-message .error-details {
  color: #f50900;
  border-color: #eec2c1;
  background-color: #f9e9e8;
}
.settings-view .error-message .error-details .alert-link {
  color: inherit;
  text-decoration: underline;
}
.settings-view .error-message .error-link {
  color: inherit;
  text-decoration: underline;
  margin-left: 6px;
}
.settings-view select.form-control {
  color: #555555;
  border-color: rgba(255, 255, 255, 0.28);
  background-color: #ffffff;
}
.settings-view select.form-control:hover,
.settings-view select.form-control:focus {
  box-shadow: none;
  background-color: #ffffff;
}
.settings-view .checkbox {
  font-size: 13px;
  padding-left: 28.8px;
}
.settings-view .checkbox .setting-title {
  display: inline-block;
}
.settings-view input[type="checkbox"] {
  -webkit-appearance: none;
  display: inline-block;
  position: relative;
  font-size: inherit;
  margin: 0 0 0 -28.8px;
  width: 1.2em;
  height: 1.2em;
  cursor: pointer;
  outline: 0;
  border-radius: 4px;
  background-color: #999999;
  transition: background-color 0.16s cubic-bezier(0.5, 0.15, 0.2, 1);
}
.settings-view input[type="checkbox"]:focus {
  box-shadow: 0 0 0 1px #b0b0b0;
}
.settings-view input[type="checkbox"]:active {
  background-color: #6dcc50;
}
.settings-view input[type="checkbox"]:before,
.settings-view input[type="checkbox"]:after {
  content: "";
  position: absolute;
  top: .9em;
  left: .45em;
  height: .15em;
  min-height: 2px;
  border-radius: 1px;
  background-color: #f8f8f8;
  transform-origin: 0% 0%;
  opacity: 0;
  transition: transform 0.1s cubic-bezier(0.5, 0.15, 0.2, 1), opacity 0.1s cubic-bezier(0.5, 0.15, 0.2, 1);
}
.settings-view input[type="checkbox"]:before {
  width: .4em;
  transform: rotate(225deg) scale(0);
}
.settings-view input[type="checkbox"]:after {
  width: .8em;
  margin: -1px;
  transform: rotate(-45deg) scale(0);
  transition-delay: .05s;
}
.settings-view input[type="checkbox"]:checked {
  background-color: #6dcc50;
}
.settings-view input[type="checkbox"]:checked:active {
  background-color: #999999;
}
.settings-view input[type="checkbox"]:checked:before,
.settings-view input[type="checkbox"]:checked:after {
  opacity: 1;
}
.settings-view input[type="checkbox"]:checked:before {
  transform: rotate(225deg) scale(1);
  transition-delay: .05s;
}
.settings-view input[type="checkbox"]:checked:after {
  transform: rotate(-45deg) scale(1);
  transition-delay: 0;
}
.settings-view .color {
  font-size: 13px;
  padding-left: 5em;
}
.settings-view .color .setting-title {
  margin-top: .15em;
}
.settings-view input[type="color"] {
  float: left;
  margin: 0 0 0 -5em;
  padding: 0;
  width: 4em;
  height: 1.6em;
  border-radius: 4px;
  border: 1px solid #b0b0b0;
  background: #f3f3f3;
}
.settings-view input[type="color"]::-webkit-color-swatch-wrapper {
  padding: 2px;
  margin: 0;
  border-radius: inherit;
}
.settings-view input[type="color"]::-webkit-color-swatch {
  border-radius: 2px;
  border: 1px solid rgba(0, 0, 0, 0.1);
}
.settings-view .editor-container {
  margin: 24px 0;
}
.settings-view .editor-container:last-child {
  margin-bottom: 0;
}
.settings-view .editor-container.settings-filter {
  margin-bottom: 36px;
}
.settings-view .section {
  padding: 48px;
  border-top: 1px solid #b0b0b0;
}
.settings-view .section:first-child,
.settings-view .section:last-child {
  padding: 48px;
}
.settings-view .section.section:empty {
  padding: 0;
  border-top: none;
}
.settings-view .section atom-text-editor[mini],
.settings-view .section atom-text-editor[mini]::shadow {
  opacity: .75;
}
.settings-view .section atom-text-editor[mini] .selection .region,
.settings-view .section atom-text-editor[mini]::shadow .selection .region {
  background-color: rgba(0, 0, 0, 0.2);
}
.settings-view .section atom-text-editor[mini].is-focused,
.settings-view .section atom-text-editor[mini].is-focused::shadow {
  opacity: 1;
}
.settings-view .section atom-text-editor[mini].is-focused .selection .region,
.settings-view .section atom-text-editor[mini].is-focused::shadow .selection .region {
  background-color: #daf1fd;
}
.settings-view div > section.section.settings-panel {
  border-top: 1px solid #b0b0b0;
}
.settings-view div > form.general-panel + .settings-panel {
  border-top: none;
}
.settings-view .section-container {
  width: 100%;
  max-width: 800px;
}
.settings-view .section-container.updates-container .package-container > .row {
  margin-left: -12px;
  margin-right: -12px;
}
.settings-view section .section-heading,
.settings-view .section .section-heading {
  margin-bottom: 24px;
  color: #333333;
  font-size: 20px;
  font-weight: bold;
  line-height: 1;
  -webkit-user-select: none;
  cursor: default;
}
.settings-view .sub-section-heading.icon:before,
.settings-view .section-heading.icon:before {
  margin-right: 9.6px;
}
.settings-view .section-heading-count {
  margin-left: .5em;
}
.settings-view .sub-section {
  margin: 48px 0;
}
.settings-view .sub-section-heading {
  color: #333333;
  font-size: 16px;
  font-weight: bold;
  line-height: 1;
}
.settings-view .control-label {
  -webkit-user-select: none;
  cursor: default;
}
.settings-view .setting-title {
  -webkit-user-select: none;
}
.settings-view .setting-description {
  color: #999999;
  font-size: 10px;
  -webkit-user-select: none;
  cursor: default;
}
.settings-view .setting-description:empty {
  display: none;
}
.settings-view .link {
  color: #4bb8f3;
}
.settings-view .link:hover,
.settings-view .link:focus {
  color: #5ec0f4;
}
.settings-view .config-menu {
  flex: 1.6;
  min-width: 180px;
  max-width: 240px;
  user-select: none;
  padding-top: 12px;
  background-color: #fdfdfd;
  border-right: 1px solid #b0b0b0;
  overflow-x: auto;
}
.settings-view .config-menu .icon:before {
  text-align: center;
}
.settings-view .config-menu .panels-packages {
  flex-grow: 1;
  overflow: auto;
  height: 0;
}
.settings-view .config-menu .panels-packages li + li {
  margin-top: 0;
}
.settings-view .config-menu .panels-packages li > a {
  border-radius: 1px !important;
  padding: 12px 14px;
  font-size: 13px;
  font-weight: bold;
  line-height: 16px;
  opacity: .85;
  transition: opacity 0.1s ease-in;
  box-shadow: inset 0 -1px 0 rgba(0, 0, 0, 0.07);
}
.settings-view .config-menu .panels-packages li > a:hover {
  opacity: 1;
  box-shadow: inset 0 -1px 0 rgba(0, 0, 0, 0.07) !important;
}
.settings-view .config-menu .panels-packages li:first-child {
  margin-top: 2px;
}
.settings-view .config-menu .panels-packages li:last-child {
  margin-bottom: 10px;
}
.settings-view .config-menu .panels-packages li.active a,
.settings-view .config-menu .panels-packages li.active a:hover {
  opacity: 1;
  box-shadow: 0 0 0 1px #225cc4 !important;
}
.settings-view .config-menu .panels-packages li.active a .package-author,
.settings-view .config-menu .panels-packages li.active a:hover .package-author,
.settings-view .config-menu .panels-packages li.active a .package-version,
.settings-view .config-menu .panels-packages li.active a:hover .package-version {
  color: #fff;
  opacity: .6;
}
.settings-view .config-menu .panels-packages .package-version {
  font-size: 11px;
  line-height: 16px;
  margin-left: 5px;
  font-weight: normal;
  color: #999;
}
.settings-view .config-menu .panels-packages .package-author {
  font-size: 11px;
  font-weight: normal;
  display: block;
  color: #999;
}
.settings-view .config-menu .button-area {
  margin: 15px;
}
.settings-view .config-menu .button-area > .btn {
  width: 100%;
}
.settings-view .config-menu .nav > li {
  margin-top: 0;
}
.settings-view .config-menu .nav > li > a {
  border-radius: 0;
  white-space: nowrap;
}
.settings-view .config-menu .nav > li > a:hover {
  color: #333333;
  background-color: #d0e6e7;
}
.settings-view .config-menu .nav > li.active > a {
  color: #ffffff;
  background-color: #0364e3;
}
.settings-view .panels {
  flex: 8.4;
  flex-flow: column;
  display: flex;
  position: relative;
  overflow: auto;
}
.settings-view .panels > div {
  flex: 1;
  min-width: 372px;
}
.settings-view .settings-panel label {
  color: #555555;
  font-size: 13px;
}
.settings-view .settings-panel .control-group + .control-group {
  margin-top: 18px;
}
.settings-view .settings-panel .control-group .editor-container {
  margin: 0;
}
.settings-view .settings-panel .grammar-filetypes {
  margin-top: 12px;
}
.settings-view .installed-package-view .package-keymap-table,
.settings-view .installed-package-view .package-grammars-table,
.settings-view .installed-package-view .package-snippets-table {
  padding-top: 12px;
  padding-left: 12px;
}
.settings-view .installed-package-view .package-keymap-table td,
.settings-view .installed-package-view .package-grammars-table td,
.settings-view .installed-package-view .package-snippets-table td {
  white-space: nowrap;
}
.settings-view .installed-package-view .package-keymap-table .snippet-body,
.settings-view .installed-package-view .package-grammars-table .snippet-body,
.settings-view .installed-package-view .package-snippets-table .snippet-body,
.settings-view .installed-package-view .package-keymap-table .snippet-prefix,
.settings-view .installed-package-view .package-grammars-table .snippet-prefix,
.settings-view .installed-package-view .package-snippets-table .snippet-prefix {
  font-family: monospace;
}
.settings-view .installed-package-view .update-message {
  padding-right: 12px;
}
.settings-view .installed-package-view .link,
.settings-view .installed-package-view .text {
  margin: 0 15px 12px 15px;
}
.settings-view .installed-package-view .update-link:hover,
.settings-view .installed-package-view .repo-link:hover {
  cursor: pointer;
  text-decoration: underline;
}
.settings-view .themes-panel .control-group {
  margin-top: 36px;
}
.settings-view .themes-panel .themes-picker {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -12px;
}
.settings-view .themes-panel .themes-picker-item {
  flex: 1;
  min-width: 320px;
  padding: 0 12px;
}
.settings-view .packages {
  height: 100%;
  display: flex;
  flex-flow: column;
}
.settings-view .packages .themes-label {
  font-weight: bold;
  font-size: 14px;
}
.settings-view .packages .theme-chooser {
  padding-top: 24px;
}
.settings-view .packages .theme-description {
  -webkit-user-select: none;
  cursor: default;
}
.settings-view .packages .package-container {
  width: 100%;
}
.settings-view .packages .search-container {
  display: flex;
  flex-wrap: wrap;
  margin: 0 -6px;
  padding-top: 18px;
}
.settings-view .packages .search-container .editor-container {
  flex: 1;
  min-width: 130px;
  margin: 6px;
}
.settings-view .packages .search-container .btn-group {
  margin: 6px;
  padding-left: 1px;
}
.settings-view .packages .search-message,
.settings-view .packages .featured-message {
  margin: 12px 12px 0 12px;
}
.settings-view .packages .error-message {
  padding: 12px;
}
.settings-view .btn-retry {
  margin-left: 12px;
}
.settings-view .readme {
  margin-top: 12px;
}
.settings-view .panel-heading .label {
  margin-left: 12px;
}
.settings-view .loading-area span:before {
  font-size: 1.1em;
  width: 1.1em;
  height: 1.1em;
}
.settings-view .error-view {
  margin: 12px 0 12px 0;
}
.settings-view .packages-panel .nav .badge {
  margin-left: 0;
}
.settings-view .copy-icon {
  cursor: pointer;
  padding-right: 2px;
}
.settings-view .copy-icon:hover {
  color: #2a6496;
}
.settings-view .table table {
  width: 100%;
  table-layout: initial;
}
.settings-view .table th {
  padding: 10px 12px;
  padding-left: 0;
}
.settings-view .table td {
  height: 1.4;
}
.settings-view .table td,
.settings-view .table th {
  padding-left: 1%;
  padding-right: 1%;
  white-space: nowrap;
  overflow: hidden;
  border-color: #b0b0b0;
}
.settings-view .table col.keystroke {
  width: 13%;
}
.settings-view .table col.command {
  width: 38%;
}
.settings-view .table col.source {
  width: 8%;
}
.settings-view .table col.selector {
  width: 33%;
}
.settings-view .compile-tools-heading {
  font-weight: bold;
}
.settings-view .compile-tools-message {
  padding-top: 12px;
}
.settings-view .compile-tools-message code {
  text-shadow: none;
  border-width: 1px;
  border-style: solid;
}
@media all and (max-width: 800px) {
  .thumbnail .btn-toolbar .btn {
    width: 100%;
    margin: 2px 0;
  }
}
.available-updates-status {
  padding-left: 2px;
}
.settings-view .container {
  padding: 0;
}
.settings-view .container > .row {
  margin-left: 0;
  margin-right: 0;
}
.settings-view .update-all-button {
  top: -4px;
  right: 0;
  position: relative;
}
.settings-view .keybinding-panel .is-user {
  color: #ffffff;
  background-color: #0364e3;
}
.settings-view .btn-wrap-group .btn {
  margin: 0 6px 6px 0;
}
.clearfix:before {
  display: table;
  content: "";
}
.clearfix:after {
  display: table;
  clear: both;
  content: "";
}
@media (min-width: 768px) {
  .container {
    width: auto;
  }
}
@media (min-width: 992px) {
  .container {
    width: auto;
  }
}
@media (min-width: 1200px) {
  .container {
    width: auto;
  }
  .col-lg-8 {
    width: 100%;
  }
}
