css:
.btn-padding-left {
  margin-left: 10px;
}
.connection-dialog .form-group {
  margin-bottom: 0;
}
.data-atom {
  position: relative;
  height: 100%;
  overflow: hidden;
  cursor: default;
  -webkit-user-select: none;
  min-height: 100px;
  height: 200px;
  z-index: 2;
  margin: 0;
}
.data-atom .resize-handle {
  position: absolute;
  width: 100%;
  height: 10px;
  top: -5px;
  cursor: row-resize;
  z-index: 3;
}
.data-atom .scrollable {
  overflow: scroll;
}
.data-atom tr {
  max-height: 1em;
}
.data-atom tr:nth-child(even) {
  background-color: #292c2f;
}
.data-atom tr:nth-child(even) td {
  border-right: 1px solid #1d1f21;
}
.data-atom td:first-child {
  background-color: #43484d;
  border-bottom: 1px solid #292c2f;
}
.data-atom td {
  padding-top: 3px;
  padding-bottom: 3px;
  padding-left: 7px;
  padding-right: 7px;
  border-right: 1px solid #292c2f;
  overflow: hidden;
  white-space: nowrap;
  text-overflow: ellipsis;
  max-width: 500px;
}
.data-atom th {
  padding: 7px;
  border-right: 1px solid #292c2f;
  border-bottom: 1px solid #292c2f;
  background-color: #43484d;
}
.data-atom .results-header select {
  width: 250px;
  margin-left: 10px;
}
.data-atom .results-header button {
  margin-left: 10px;
  top: -1px;
}
